package edu.unlv.mis.kly.w3;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 1;
		
		while (number <= 5) {
			System.out.println("Hello");
			number++;
		}

	}

}

